CREATE TABLE CURSOS (COD_CUR VARCHAR(10) PRIMARY KEY NOT NULL, DESCRIPCION VARCHAR(30), TURNO VARCHAR(1), TUTOR VARCHAR(30));
CREATE TABLE ALUMNOS (COD_ALU VARCHAR(10) PRIMARY KEY NOT NULL, COD_CUR VARCHAR(10) NOT NULL, DNI VARCHAR(10), APELLIDOS VARCHAR(30), NOMBRE VARCHAR(20), CONSTRAINT FKCOD_CUR_ALUMNOS_CURSOS FOREIGN KEY (COD_CUR) REFERENCES CURSOS);
CREATE TABLE NOTAS (COD_CUR VARCHAR(10) NOT NULL, COD_ALU VARCHAR(10) NOT NULL, NOTA1 INT, NOTA2 INT, NOTA3 INT, MEDIA INT, PRIMARY KEY (COD_CUR, COD_ALU), CONSTRAINT FKCOD_CUR_NOTAS_CURSOS FOREIGN KEY (COD_CUR) REFERENCES CURSOS, CONSTRAINT FKCOD_ALU_NOTAS_ALUMNOS FOREIGN KEY (COD_ALU) REFERENCES ALUMNOS);
